import pytest

from analyser.utils.helpers import common_inputs


def test_common_inputs():
    psbt_base64 = ""

    # test common inputs
    result = common_inputs(psbt_base64)

    # check that the inputs and outputs lists are sorted
    assert result["inputs"] == [1500000240, 2500000000, 2500000000]
    assert result["outputs"] == [450000000, 500000000, 5000000000]

    # check that common inputs are detected
    assert result["status"] is True
    assert result["message"] == "Common Inputs Detected"

    # test Uncommon inputs

    psbt_base64 = ""
    result = common_inputs(psbt_base64)

     # check that the inputs and outputs lists are sorted
    assert result["inputs"] == [1500000240, 2500000000, 2500000000]
    assert result["outputs"] == [450000000, 1450000000, 2000000000, 2500000000]

    # check that common inputs are detected
    assert result["status"] is False
    assert result["message"] == "No Common Inputs Detected"

    # test without single input
    psbt_base64 =""
    result = common_inputs(psbt_base64)



    # check that common inputs are detected s
    assert result["status"] is True
    assert result["message"] == "Common Inputs Detected"

     # test with two common inputs
    psbt_base64 =""
    result = common_inputs(psbt_base64)

     # check that the inputs and outputs lists are sorted
    assert result["inputs"] == [1500000240, 2500000000]
    assert result["outputs"] == [480000000, 3500000000]

    # check that common inputs are detected s
    assert result["status"] is True
    assert result["message"] == "Common Inputs Detected"
